// src/components/TtsForm.js
import React, { useState } from 'react';

const TtsForm = ({ onGenerateAudio, onUploadFiles }) => {
  const [prompt, setPrompt] = useState('');
  const [persona, setPersona] = useState('');
  const [voice, setVoice] = useState('coral');
  const [files, setFiles] = useState([]);

  const handlePromptChange = (e) => {
    setPrompt(e.target.value);
  };

  const handlePersonaChange = (e) => {
    setPersona(e.target.value);
  };

  const handleVoiceChange = (e) => {
    setVoice(e.target.value);
  };

  const handleFileUpload = (e) => {
    setFiles(Array.from(e.target.files));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onGenerateAudio(prompt, persona, voice);
    onUploadFiles(files);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="prompt">Prompt:</label>
        <input type="text" id="prompt" value={prompt} onChange={handlePromptChange} />
      </div>
      <div>
        <label htmlFor="persona">Persona:</label>
        <input type="text" id="persona" value={persona} onChange={handlePersonaChange} />
      </div>
      <div>
        <label htmlFor="voice">Voice:</label>
        <select id="voice" value={voice} onChange={handleVoiceChange}>
          <option value="coral">Coral</option>
          <option value="echo">Echo</option>
          {/* Add other voice options */}
        </select>
      </div>
      <div>
        <input type="file" multiple id="files" onChange={handleFileUpload} />
      </div>
      <button type="submit">Generate Audio from Prompt</button>
      <button type="button" onClick={onUploadFiles}>Upload .xlsx File</button>
    </form>
  );
};

export default TtsForm;
