// src/App.js
import React, { useState } from 'react';
import Header from './components/Header';
import TtsForm from './components/TtsForm';
import AudioList from './components/AudioList';
import Footer from './components/Footer';

const App = () => {
  const [audioFiles, setAudioFiles] = useState([]);

  const generateAudioFromPrompt = (prompt, persona, voice) => {
    // Implement logic to generate audio using OpenAI API
    const newFile = {
      name: `${prompt.replace(/[^a-zA-Z0-9]/g, '_')}.mp3`,
      prompt,
      url: 'data:audio/mpeg;base64,YOUR_BASE64_AUDIO_HERE',
      checked: false,
    };
    setAudioFiles([...audioFiles, newFile]);
  };

  const handleUploadFiles = (files) => {
    // Implement logic to parse .xlsx files and generate TTS
    Array.from(files).forEach(file => {
      if (file.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
        // Placeholder function to parse the spreadsheet and generate TTS
        const promptText = parseXlsx(file); // Placeholder function
        const newFile = {
          name: `${promptText.replace(/[^a-zA-Z0-9]/g, '_')}.mp3`,
          prompt: promptText,
          url: 'data:audio/mpeg;base64,YOUR_BASE64_AUDIO_HERE',
          checked: false,
        };
        setAudioFiles([...audioFiles, newFile]);
      }
    });
  };

  const handleDownload = (index) => {
    const selectedFiles = audioFiles.filter((_, idx) => idx === index);
    if (selectedFiles.length > 0) {
      selectedFiles.forEach(file => downloadFile(file));
    } else {
      // Logic to download all files
      audioFiles.forEach(file => downloadFile(file));
    }
  };

  const downloadFile = (file) => {
    const url = URL.createObjectURL(file.url);
    const a = document.createElement('a');
    a.href = url;
    a.download = file.name;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className="App">
      <Header />
      <main>
        <TtsForm
          onGenerateAudio={generateAudioFromPrompt}
          onUploadFiles={handleUploadFiles}
        />
        <AudioList audioFiles={audioFiles} onDownload={handleDownload} />
      </main>
      <Footer />
    </div>
  );
};

export default App;
