// src/components/AudioList.js
import React, { useState } from 'react';

const AudioList = ({ audioFiles, onDownload }) => {
  return (
    <div>
      <h2>Playlist</h2>
      <table>
        <thead>
          <tr>
            <th>File Name</th>
            <th>Length</th>
            <th>Prompt Text</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {audioFiles.map((file, index) => (
            <tr key={index}>
              <td>{file.name}</td>
              <td>{'0:00'}</td>
              <td>{file.prompt}</td>
              <td>
                <button onClick={() => playAudio(file)}>Play</button>
                <button onClick={() => onDownload(index)}>Download</button>
                <input type="checkbox" id={`checkbox-${index}`} onChange={(e) => file.checked = e.target.checked} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const playAudio = (file) => {
  const audio = new Audio(URL.createObjectURL(file.url));
  audio.play();
};

export default AudioList;
